extern crate libnv;

#[cfg(feature = "nvpair")]
pub fn main() {
    use libnv::nvpair::NvList;

    let val = "";
    let mut list = NvList::default();
    list.insert_string("is_it_ready?", val).unwrap();
    let ret = list.get_string("is_it_ready?").unwrap();
    assert_eq!(val, &ret);
}

#[cfg(not(feature = "nvpair"))]
pub fn main() {}
